                    if(errSet){
                        res.status(500).json({error: errSet});                             
                        return;    
                    }else{
                        if(updated === null){
                             res.status(400).json({ "error": { "message": "Already redeemed!" } });
                        }else{
                            res.status(200).json(updated);      
                        }
                    }
                });
            }else{
                res.status(400).json({errors: ['Not found']});
            }                
        });
    }else{
        res.send("Not a valid Date Format");
    }
 });

